import React, { Component } from "react";
import "./Home.css";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      binary: '11110000',
      decimal: '240'
    };

    this.handleBinChange = this.handleBinChange.bind(this);
    //this.handleDecChange = this.handleDecChange.bind(this);
  }

  handleBinChange(e) {
    var i;
    var decimalValue;
    //Check for ones and zeros, if true, then start adding)
    console.log("Binary field length: " + e.target.value.length);
    for(i = 0; i < e.target.length; i++) {
      if(e.target.value[i]==="0" || e.target.value[i]==="1")
      {
	decimalValue+=i**2;
	console.log("decimalValue: " + decimalValue);
      }
      else{
        decimalValue = "Not a valid binary number!"
        break;
      }
    }
    this.setState({
      binary: e.target.value,
      decimal: decimalValue 
    });
  }

  //handleDecChange(e) {
    //this.setState({
      //binary: e.target.value
    //});
  //}

  render() {
    return (
      <div className="Home">
        <div className="lander">
          <h1>Bin2Dec</h1>
          <div>
	    Enter Binary: <input type="text" name="binary" value={this.state.binary} onChange={this.handleBinChange} />
          </div>
	  <div>
	    Enter Decimal: <input type="text" value={this.state.decimal} />
	  </div>
        </div>
      </div>
    );
  }
}
