import React, { Component } from "react";
import "./Home.css";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      binary: '11110000',
      decimal: '240'
    };

    this.handleBinChange = this.handleBinChange.bind(this);
    //this.handleDecChange = this.handleDecChange.bind(this);
  }

  handleBinChange(e) {
    this.setState({
      binary: e.target.value,
      decimal: e.target.value
    });
  }

  //handleDecChange(e) {
    //this.setState({
      //binary: e.target.value
    //});
  //}

  render() {
    return (
      <div className="Home">
        <div className="lander">
          <h1>Bin2Dec</h1>
          <div>
	    Enter Binary: <input type="text" name="binary" value={this.state.binary} onChange={this.handleBinChange} />
          </div>
	  <div>
	    Enter Decimal: <input type="text" value={this.state.decimal} />
	  </div>
        </div>
      </div>
    );
  }
}
